CNVR.flashVars = (function () {			var arr = [];			var loc = location.href;			if (loc.indexOf("http") > -1 && (loc.indexOf("cfs-c01") > -1 || loc.indexOf("opslocal-c01") >-1)){				loc = loc.substring(0, loc.lastIndexOf("/"));
				arr.push({name: "company_id", value: loc});
			}
			arr.push({name: "force_retina", value: "false&320x480=true&320x50=true"});
			arr.push({name: "base_url", value: ""});
			/* START VARS */
			/* END VARS */
			var tmp = "";
			for (var i = 0; i < arr.length; i++) {
				tmp = tmp + arr[i].name + "=" + encodeURIComponent(arr[i].value).replace(/'/g, "%27").replace(/"/g, "%22");
				if (i + 1 < arr.length) {
					tmp = tmp + "&amp;"
			}	
			}
			if (tmp !== "") {
				tmp = "&amp;" + tmp;
			}
			return tmp;
			})();
